import './App.css';
import Banner from './Components/Banner';
import Home from './Components/Home';
import Footer from './Components/Footer';
import Reddit from './Components/Reddit';
//import Courses from './Components/Courses';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import React, { Component  } from 'react';
import axios from 'axios';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      course: null,
      courseList: null
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    axios.get("/api/list").then((result) => this.setState({ courseList: result }, this.parseCourseList));
 }

 parseCourseList() {
     let courseArr = this.state.courseList.data;
     for (let i = 0; i < courseArr.length; i++) {

         let num = courseArr[i].course.num;
         let courseNum = num.split(/(\s+)/).filter( e => e.trim().length > 0).join(" ");
         if (courseNum === searched) {
             this.setState({ foundCourse: courseArr[i]});
             console.log(courseArr[i]);
         }
     }
 }

  handleChange(search) {
    this.setState({ course: search });
  }

  render() {
    const course = this.state.course;
    return (
      <div className="App">
      <Router>
        <h1>{course}</h1>
        <Banner courseList={this.courseList} value={course} onChange={this.handleChange} />
        <Switch>
          <Route path="/" exact component={Home} />
        </Switch>
        <Footer />
      </Router>
    </div>
    );
  }
}

export default App;
