import './App.css';
import Banner from './Components/Banner';
import Home from './Components/Home';
import Footer from './Components/Footer';
import Reddit from './Components/Reddit';
//import Courses from './Components/Courses';
import { BrowserRouter as Router, Switch, Route, useHistory } from 'react-router-dom';
import React, { Component  } from 'react';
import axios from 'axios';
import Courses from './Components/Courses';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      course: null,
      courseApiData: null,
      courseList: null
    };
    this.history = useHistory;
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    axios.get("/api/list").then((result) => this.setState({ ...this.state.course, courseApiData: result, ...this.state.courseList }, this.parseCourseList));
 }

 parseCourseList() {
     let courseArr = this.state.courseApiData.data;
     let niceList = [];
     for (let i = 0; i < courseArr.length; i++) {
         let num = courseArr[i].course.num;
         let courseNum = num.split(/(\s+)/).filter( e => e.trim().length > 0).join(" ");
         niceList.push(courseNum);
     }
     console.log("getting course list");
     this.setState({ ...this.state.course, ...this.state.courseApiData, courseList: niceList });
 }

  handleChange(search) {
    this.setState({ course: search, ...this.state.courseApiData, ...this.courseList });
    this.history.push("/about");
  }

  render() {
    const course = this.state.course;
    return (
      <div className="App">
      <Router>
        <h1>{course}</h1>
        <Banner courseList={this.state.courseList} value={course} onChange={this.handleChange} />
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/search" exact component={Courses} />
        </Switch>
        <Footer />
      </Router>
    </div>
    );
  }
}

export default App;
